import { Link } from "react-router-dom";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Modal from "react-bootstrap/Modal";
import { useState } from "react";

const TopNav = () => {
  const [showLoginModal, setLoginModal] = useState(false);
  const [showSignupModal, setSignupModal] = useState(false);

  const openLoginModal = e => {
    e.preventDefault();
    console.log("clicked");
    setLoginModal(true);
  };

  const closeLoginModal = () => {
    setLoginModal(false);
  };

  const openSignupModal = e => {
    e.preventDefault();
    setSignupModal(true);
  };

  const closeSignupModal = () => {
    setLoginModal(false);
  };

  return (
    <Navbar bg="dark" variant="dark">
      <Navbar.Brand as={Link} to="/">
        Private Forum
      </Navbar.Brand>
      <Nav.Link href="#" className="ml-auto" onClick={openLoginModal}>
        Login
      </Nav.Link>
      <Nav.Link href="#" onClick={openSignupModal}>
        Sign Up
      </Nav.Link>
      <Modal show={showLoginModal} onHide={closeLoginModal}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <Modal.Body>Login Form</Modal.Body>
      </Modal>
      <Modal show={showSignupModal} onHide={closeSignupModal}></Modal>
    </Navbar>
  );
};

export default TopNav;
