import { convertDate } from "../helpers";

const Comment = ({ post }) => {
  return (
    <div>
      <h3>Comments</h3>
      {post.comments.map(comment => (
        <div key={comment._id}>
          <p>{comment.body}</p>
          <small>
            - {comment.user.username} on {convertDate(comment.createdAt)}
          </small>
        </div>
      ))}
    </div>
  );
};

export default Comment;
